(function(document,window){"use strict";var rows=[];var timeoutHandle;var triggerEvent=function(el,eventName,detail){var event=document.createEvent("CustomEvent");event.initCustomEvent(eventName,true,true,detail);el.dispatchEvent(event)};var renderHelpDiv=function(){var helpDiv=document.getElementById("impress-help");if(helpDiv){var html=[];for(var row in rows){for(var arrayItem in row){html.push(rows[row][arrayItem])}}if(html){helpDiv.innerHTML="<table>\n"+html.join("\n")+"</table>\n"}}};var toggleHelp=function(){var helpDiv=document.getElementById("impress-help");if(!helpDiv){return}if(helpDiv.style.display==="block"){helpDiv.style.display="none"}else{helpDiv.style.display="block";window.clearTimeout(timeoutHandle)}};document.addEventListener("keyup",function(event){if(event.target.nodeName==="BODY"||event.target.nodeName==="HTML"){if(event.keyCode===72){event.preventDefault();toggleHelp()}}},false);document.addEventListener("impress:help:add",function(e){var rowIndex=e.detail.row;if(typeof rows[rowIndex]!=="object"||!rows[rowIndex].isArray){rows[rowIndex]=[]}rows[e.detail.row].push("<tr><td><strong>"+e.detail.command+"</strong></td><td>"+e.detail.text+"</td></tr>");renderHelpDiv()});document.addEventListener("impress:init",function(e){renderHelpDiv();var helpDiv=document.getElementById("impress-help");if(helpDiv){helpDiv.style.display="block";timeoutHandle=window.setTimeout(function(){var helpDiv=document.getElementById("impress-help");helpDiv.style.display="none"},7e3);var api=e.detail.api;api.lib.gc.pushCallback(function(){window.clearTimeout(timeoutHandle);helpDiv.style.display="";helpDiv.innerHTML="";rows=[]})}triggerEvent(document,"impress:help:add",{command:"H",text:"Show this help",row:0})})})(document,window);