(function(document,window){"use strict";var util;document.addEventListener("impress:init",function(event){util=event.detail.api.lib.util},false);var getNextStep=function(el){var steps=document.querySelectorAll(".step");for(var i=0;i<steps.length;i++){if(steps[i]===el){if(i+1<steps.length){return steps[i+1]}else{return steps[0]}}}};var getPrevStep=function(el){var steps=document.querySelectorAll(".step");for(var i=steps.length-1;i>=0;i--){if(steps[i]===el){if(i-1>=0){return steps[i-1]}else{return steps[steps.length-1]}}}};var skip=function(event){if(!event||!event.target){return}if(event.detail.next.classList.contains("skip")){if(event.detail.reason==="next"){event.detail.next=getNextStep(event.detail.next);skip(event)}else if(event.detail.reason==="prev"){event.detail.next=getPrevStep(event.detail.next);skip(event)}event.detail.transitionDuration=util.toNumber(event.detail.next.dataset.transitionDuration,event.detail.transitionDuration)}};window.impress.addPreStepLeavePlugin(skip,1)})(document,window);