(function(document,window){"use strict";var startX=0;var lastX=0;var lastDX=0;var threshold=window.innerWidth/20;document.addEventListener("touchstart",function(event){lastX=startX=event.touches[0].clientX});document.addEventListener("touchmove",function(event){var x=event.touches[0].clientX;var diff=x-startX;lastDX=lastX-x;lastX=x;window.impress().swipe(diff/window.innerWidth)});document.addEventListener("touchend",function(){var totalDiff=lastX-startX;if(Math.abs(totalDiff)>window.innerWidth/5&&totalDiff*lastDX<=0){if(totalDiff>window.innerWidth/5&&lastDX<=0){window.impress().prev()}else if(totalDiff<-window.innerWidth/5&&lastDX>=0){window.impress().next()}}else if(Math.abs(lastDX)>threshold){if(lastDX<-threshold){window.impress().prev()}else if(lastDX>threshold){window.impress().next()}}else{window.impress().goto(document.querySelector("#impress .step.active"))}});document.addEventListener("touchcancel",function(){window.impress().goto(document.querySelector("#impress .step.active"))})})(document,window);